def skip_member(app, what, name, obj, skip, options):
    excludes = ["__init__", "__repr__", "__weakref__", "__doc__",
                "__swig_destroy__", "__module__", "__dict__", "__metaclass__",
                "__new__", "__disown__"]
    if name[0:2] == "__" and name[-2:] == "__" and not name in excludes:
        return False
#    print "wha: ", what
#    print "nam: ", name
#    print "obj: ", obj
#    print "ski: ", skip
    return skip

